mapper.update:
UPDATE BOARD SET
HITS = HITS+1
WHERE BOARD_ID = #{BOARD_ID}

mapper.select:
SELECT * FROM (SELECT BOARD_ID, LEAD(BOARD_ID,1,9999) OVER(ORDER BY BOARD_ID) AS next, LAG(BOARD_ID,1,9999) OVER(ORDER BY BOARD_ID) AS last, TITLE, LEAD(TITLE,1,9999) OVER(ORDER BY BOARD_ID) AS nextTitle, LAG(TITLE,1,9999) OVER(ORDER BY BOARD_ID) AS lastTitle, UPLOAD_AT, CATEGORY_ID, ROW_NUMBER() OVER (ORDER BY UPLOAD_AT DESC) AS RNM FROM BOARD WHERE CATEGORY_ID = #{category_id}
ORDER BY BOARD_ID DESC
)k WHERE BOARD_ID = #{board_id}

SELECT * FROM (SELECT BOARD_ID, LEAD(BOARD_ID,1,9999) OVER(ORDER BY BOARD_ID) AS next, LAG(BOARD_ID,1,9999) OVER(ORDER BY BOARD_ID) AS last, TITLE, LEAD(TITLE,1,9999) OVER(ORDER BY BOARD_ID) AS nextTitle, LAG(TITLE,1,9999) OVER(ORDER BY BOARD_ID) AS lastTitle, UPLOAD_AT, CATEGORY_ID, ROW_NUMBER() OVER (ORDER BY UPLOAD_AT DESC) AS RNM FROM BOARD
ORDER BY BOARD_ID DESC
)k WHERE BOARD_ID = #{board_id}
