mapper.select:
SELECT * FROM ( SELECT b.BOARD_ID, b.TITLE, b.CONTENT, b.ADMIN_NUM, b.UPLOAD_AT, b.HITS, a.ADMIN_NAME AS writer, ROW_NUMBER() OVER (ORDER BY b.UPLOAD_AT DESC) AS RNM FROM BOARD b JOIN ADMIN a ON a.ADMIN_NUM = b.ADMIN_NUM WHERE b.DEL_YN = 'N' AND SEPARATION = '일반'
AND b.CATEGORY_ID = #{CATEGORY_ID}
) t WHERE RNM BETWEEN #{pageDto.start} AND #{pageDto.end}

mapper.update:
UPDATE BOARD SET
HITS = HITS+1
WHERE BOARD_ID = #{BOARD_ID}

mapper.select:
SELECT * FROM (SELECT BOARD_ID, LEAD(BOARD_ID,1,9999) OVER(ORDER BY BOARD_ID) AS next, LAG(BOARD_ID,1,9999) OVER(ORDER BY BOARD_ID) AS last, TITLE, LEAD(TITLE,1,9999) OVER(ORDER BY BOARD_ID) AS nextTitle, LAG(TITLE,1,9999) OVER(ORDER BY BOARD_ID) AS lastTitle, UPLOAD_AT, CATEGORY_ID, ROW_NUMBER() OVER (ORDER BY UPLOAD_AT DESC) AS RNM FROM BOARD WHERE DEL_YN = 'N'
AND CATEGORY_ID = #{CATEGORY_ID}
ORDER BY BOARD_ID DESC )k WHERE BOARD_ID = #{board_id}
