mapper.select:
select m.*,n.* from menu m, nutrient n
where n.menu_id = m.menu_id
and m.menu_id = #{menu_id}

mapper.update:
BEGIN insert into menu values (menu_num.nextval,#{mnDto.menu_title},#{mnDto.menu_eng_title},#{mnDto.menu_content},sysdate,sysdate,#{mnDto.menu_del},#{mnDto.menu_ice_hot},#{mnDto.menu_onesize},#{mnDto.menu_file_id},1,#{mnDto.category_id});
insert into nutrient values (nutrient_num.nextval,#{mnDto.nut_saturated},#{mnDto.nut_sugar},#{mnDto.nut_sodium},#{mnDto.nut_protein},#{mnDto.nut_caffeine},#{mnDto.nut_calorie},#{mnDto.nut_high_caffeine},#{mnDto.nut_size},#{mnDto.nut_unit},menu_num.currval);
into menu_type values (menu_num.currval,#{type})
into menu_allergen values (menu_num.currval,#{allergen})
END;

BEGIN
update menu
set
menu_title=#{menu_title},menu_eng_title=#{menu_eng_title},menu_content=#{menu_content},menu_update=sysdate,menu_ice_hot=#{menu_ice_hot},menu_onesize=#{menu_onesize},menu_file_id=#{menu_file_id},category_id=#{category_id},menu_del=#{menu_del}
where menu_id=#{menu_id};

update nutrient
set
nut_saturated=#{nut_saturated},nut_sugar=#{nut_sugar},nut_sodium=#{nut_sodium},nut_protein=#{nut_protein},nut_caffeine=#{nut_caffeine},nut_calorie=#{nut_calorie},nut_high_caffeine=#{nut_high_caffeine},nut_size=#{nut_size},nut_unit=#{nut_unit}
where nut_id=#{nut_id};
END;

BEGIN
delete from menu_type where menu_id=#{menu_id};


		into menu_type values
		(#{menu_id},#{type})


END;

BEGIN
delete from menu_allergen where menu_id=#{menu_id};


		into menu_allergen values
		(#{menu_id},#{allergen})


END;
